import React, { useState } from 'react';
import {
    FooterContainer,
    FooterWrap,
    FooterLinksContainer,
    FooterLinksWrapper,
    FooterLinkItems,
    FooterLinkTitle,
    FooterLink,
    WebsiteRights,
    CopyButton
} from './FooterElements';
import { NavLogo } from '../Navbar/NavbarElements';
import "./footerStyle.css";

const Footer = () => {
    const [isCopied, setIsCopied] = useState(false);

    const handleCopy = () => {
        if (!isCopied) {
            setIsCopied(true);
            /* Copy the text inside the text field */
            navigator.clipboard.writeText("joncarre@ucm.es");

            setTimeout(() => {
                setIsCopied(false);
            }, 1500)
        }
    }
    return (
        <FooterContainer>
            <FooterWrap>
                <FooterLinksContainer>
                    <FooterLinksWrapper>
                        <FooterLinkItems>
                            <FooterLinkTitle>About us</FooterLinkTitle>

                            <FooterLink to="/" onClick={() => {
                                window.location.replace('http://maude.sip.ucm.es/fadoss');
                                return null;
                            }}>Research Group</FooterLink>

                            <FooterLink to="/" onClick={() => {
                                window.location.replace('https://github.com/Joncarre/Blockchain');
                                return null;
                            }}>Related projects</FooterLink>

                            <div className="tooltip">
                                <FooterLink to="/">Contact us</FooterLink>
                                <div className="bottom">
                                    <span>joncarre@ucm.es</span>
                                    <CopyButton onClick={() => handleCopy()}>
                                        <div className={!isCopied ? "show" : "hide"}>Copy</div>
                                        <div className={isCopied ? "show" : "hide"}>Copied!</div>
                                    </CopyButton>
                                    <i></i>
                                </div>
                            </div>
                        </FooterLinkItems>
                    </FooterLinksWrapper>
                    <FooterLinksWrapper>
                        <FooterLinkItems>
                            <FooterLinkTitle>The DApp</FooterLinkTitle>
                            <FooterLink to="/">How it works</FooterLink>
                            <FooterLink to="/">Examples</FooterLink>
                            <FooterLink to="/">Terms of Service</FooterLink>
                        </FooterLinkItems>
                    </FooterLinksWrapper>
                </FooterLinksContainer>
                <WebsiteRights>
                    J. Carrero © {new Date().getFullYear()} All rights reserved.
                </WebsiteRights>
            </FooterWrap>
        </FooterContainer>
    );
};

export default Footer;
