import { React, useRef } from 'react';
import { useForm } from 'react-hook-form';
import * as emailjs from "emailjs-com";
import { generatorAddress, requestAccount } from '../../App.js';
import { ethers } from 'ethers';
import Generator from '../../artifacts/contracts/Generator.sol/Generator.json';
import { 
    Container, 
    Form, 
    FormButton,
    FormError,
    FormInput, 
    FormWrap,
    FormH1,
    FormLabel,
    FormContent
 } from './RegisterElements';

const Register = () => {
    const {
        register,
        handleSubmit,
        reset,
        formState: { errors }
    } = useForm();
    
    const sendEmail = (formData, e) => {
      console.log(e.target);
      // Send email
      emailjs.sendForm("dapp_gmail", "dapp_mail_template", e.target, "user_8Njq6OjGUsVPoTG1GpG30")
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
        reset();
    };
  






    function cutHash(elem){
      var cut = elem.replace(/[^0-9]/g, '');
      var result = cut.substring(0,10);
      return result;
    }

    async function async_setResearcher(data) {
        if (typeof window.ethereum !== 'undefined') {
          await requestAccount()
          const provider = new ethers.providers.Web3Provider(window.ethereum);
          const signer = provider.getSigner()
          const contract = new ethers.Contract(generatorAddress, Generator.abi, signer);

          var secret = 21212121;
          console.log("async_setResearcher: " + secret, data.name, data.email, data.orcid);
          const transaction = await contract.setResearcher(
            secret,
            data.name, 
            data.email,
            data.orcid,
            {gasLimit: 12000000})
          await transaction.wait()
        }
    }

    return ( 
        <>
            <Container>
                <FormWrap>
                    <FormContent>
                        <form onSubmit={handleSubmit(sendEmail)}>
                            
                            <label>Name</label>
                            <input
                                {...register("name", {
                                required: true,
                                maxLength: 20,
                                pattern: /^[A-Za-z]+$/i
                            })} type="text"/>

                            <label>Email</label>
                            <input
                                {...register("email", {
                                required: true,
                                pattern: /\S+@\S+\.\S+/
                            })} type="email"/>


                            <label>ORCID</label>
                            <input {...register("orcid", { 
                                required: true,
                                minLength: 16, 
                                maxLength: 16,
                                pattern: /^[0-9\b]+$/
                            })} type="number"/>

                            <input type="submit"/>
                        </form>
                    </FormContent>
                </FormWrap>
            </Container>
        </>
    )
}

export default Register;
