import React from 'react';

const clausesLength = 3;

// Builds the instance(s) into a friendly way
export function buildInstance(arr){
    let result = [];
    // id
    result[0] = hexToDecimal(arr[0][0]);
    // chain
    result[1] = buildChain(arr[0][1]);
    // size
    result[2] = hexToDecimal(arr[0][2]);
    // date created
    //result[3] = buildDate(arr[3]);
    // solution
    //result[4] = arr[4];
    // solved
    //result[5] = arr[5];
    // date resolved
    //result[6] = buildDate(arr[6]);
    return result;
}

// Builds the chain as a string
function buildChain(_oldChain){
    let result = "";
    let array = Array.from(_oldChain);
    while(array.length > 1){
        result += "(";
        for(let i = 0; i < clausesLength; i++){
            if(getBoolean)
                result += "x" + array.shift();
            else
                result += "¬" + "x" +array.shift();
            if(i < clausesLength-1) 
                result += " v ";
        }
        result += ")";
        if(array.length > 1)
            result += " ^ ";
    }
    return result;
}

// Converts a hexadecimal number to decimal number
function hexToDecimal(_hexNumber){
    return parseInt(_hexNumber, 16);
}

// Builds the date as a date format
function buildDate(_oldDate){
    let newDate = new Date(parseInt(_oldDate._hex.slice(2), 16)*1000);
    let result = newDate.getFullYear() + "/" + newDate.getMonth() + "/" + newDate.getDate()  + " " + newDate.getHours() + ":" + newDate.getMinutes();
    return result;
}

// Return 'true' or 'false' with 50% probability
function getBoolean(){
    if(Math.random() >= 50)
        return true;
    else
        return false;
}

export default buildInstance;
