import './App.css';
import { useState, useEffect } from 'react';
import { useForm } from "react-hook-form";
import { ethers } from 'ethers';
import Generator from './artifacts/contracts/Generator.sol/Generator.json'
import Navbar from "./components/Navbar/index";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Sidebar from "./components/SideBar";
import Home from "./pages";
import SigninPage from "./pages/signin";
import RegisterPage from "./pages/register";

// Update with the contract address logged out to the CLI when it was deployed 
const generatorAddress = "0x6e4B3A201532D0FfD2d3C821371b009aA113bFcE"

function App() {

  const {
    register,
    handleSubmit,
    watch,
    formState: { errors }
  } = useForm();

  const onSubmit = (data) => {
    alert(JSON.stringify(data));
  }; // your form submit function which will invoke after successful validation

  console.log(watch("example"));

  const [researcher, setResearcher] = useState({
    orcid: null,
    registered: null,
    idInstance: null
  })

  const [instance, setInstance] = useState({
    id: null,
    chain: null,
    size: null,
    dateCreated: null,
    solution: null,
    solved: null,
    dateSolution: null
  })

  // request access to the user's MetaMask account
  async function requestAccount() {
    await window.ethereum.request({ method: 'eth_requestAccounts' });
  }

    async function async_setResearcher(data) {
      if (typeof window.ethereum !== 'undefined') {
        await requestAccount()
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner()
        const contract = new ethers.Contract(generatorAddress, Generator.abi, signer);
        const transaction = await contract.setResearcher(data.firstName, data.age)
        await transaction.wait()
      }
    }

    async function async_getResearcher() {
      if (typeof window.ethereum !== 'undefined') {
        const provider = new ethers.providers.Web3Provider(window.ethereum)
        const contract = new ethers.Contract(generatorAddress, Generator.abi, provider);
        try {
          const signer = provider.getSigner()
          const result = await contract.connect(signer).getResearcher()
          console.log(result)
        } catch (err) {
          console.log("Error: ", err)
        }
      }    
    }

  return (
    <div className="App">
      <h3>Set information</h3>
      <h3>Get information</h3>
      <button onClick={async_getResearcher}>Get researcher</button>

      <form onSubmit={handleSubmit(async_setResearcher)}>
      <label>First Name</label>
      <input
        {...register("firstName", {
          required: true,
          maxLength: 20,
          pattern: /^[A-Za-z]+$/i
        })}
      />
      {errors?.firstName?.type === "required" && <p>This field is required</p>}
      {errors?.firstName?.type === "maxLength" && (
        <p>First name cannot exceed 20 characters</p>
      )}
      {errors?.firstName?.type === "pattern" && (
        <p>Alphabetical characters only</p>
      )}
      <label>Laste Name</label>
      <input {...register("lastName", { pattern: /^[A-Za-z]+$/i })} />
      {errors?.lastName?.type === "pattern" && (
        <p>Alphabetical characters only</p>
      )}
      <label>Age</label>a
      <input {...register("age", { min: 18, max: 99 })} />
      {errors.age && (
        <p>You Must be older then 18 and younger then 99 years old</p>
      )}
      <input type="submit" />
    </form>
    </div>
     /* <Router>
        <Switch>
          <Route path='/' component={Home} exact />
          <Route path='/signin' component={SigninPage} exact />
          <Route path='/register' component={RegisterPage} exact />
        </Switch>
      </Router>
    */
  );
}

export default App;
