import React from 'react';

const clausesLength = 3;

// Builds the instance(s) into a friendly way
export function buildInstance(arr){
    let building = [];
    let result = [];
    for(let i = 0; i < arr.length; i++){
        // id
        building[0] = hexToDecimal(arr[1][0]);
        // chain
        building[1] = buildChain(arr[i][1]);
        // size
        building[2] = hexToDecimal(arr[i][2]);
        // date created
        building[3] = buildDate(arr[i][3]);
        // solution
        building[4] = arr[i][4];
        // solved
        building[5] = arr[i][5];
        // date resolved
        building[6] = buildDate(arr[i][6]);
        result[i] = building;
    }
    return result;
}

// Builds the chain as a string
function buildChain(_oldChain){
    let result = "";
    let array = Array.from(_oldChain);
    while(array.length > 1){
        result += "(";
        for(let i = 0; i < clausesLength; i++){
            if(Math.random() < 0.5)
                result += "x" + array.shift();
            else
                result += "¬" + "x" + array.shift();
            if(i < clausesLength-1) 
                result += " v ";
        }
        result += ")";
        if(array.length > 1)
            result += " ^ ";
    }
    return result;
}

// Converts a hexadecimal number to decimal number
function hexToDecimal(_hexNumber){
    return parseInt(_hexNumber, 16);
}

// Builds the date as a date format
function buildDate(_oldDate){
    let result, newDate;
    if(hexToDecimal(_oldDate) !== 0){
        newDate = new Date(parseInt(_oldDate._hex.slice(2), 16)*1000);
        result = newDate.getFullYear() + "/" + newDate.getMonth() + "/" + newDate.getDate()  + " " + newDate.getHours() + ":" + newDate.getMinutes();
    }else
        result = "Date unavailable";
    return result;
}

// Return 'true' or 'false' with 50% probability
function getBoolean(){
    if(Math.random() >= 50)
        return true;
    else
        return false;
}

export default buildInstance;
