import React, { useState, useEffect } from 'react';
import {
    FooterContainer,
    FooterWrap,
    FooterLinksContainer,
    FooterLinksWrapper,
    FooterLinkItems,
    FooterLinkTitle,
    FooterLink,
    WebsiteRights,
    CopyButton
} from './FooterElements';
import { NavLogo } from '../Navbar/NavbarElements';
import "./messagesInfo.css";

const Footer = () => {
    const [copyText, setCopyText] = useState("Copy");
    const [isCopied, setIsCopied] = useState(false);

    // useEffect(() => {
    //     setIsCopied(copyText === "Copied!" ? true : false);
    // }, [copyText])

    const handleCopy = () => {
        setCopyText("Copied!");
        setIsCopied(true);
        setTimeout(() => {
            setCopyText("Copy");
            setIsCopied(false);
        }, 1200)
    }
    return (
        <FooterContainer>
            <FooterWrap>
                <FooterLinksContainer>
                    <FooterLinksWrapper>
                        <FooterLinkItems>
                            <FooterLinkTitle>About us</FooterLinkTitle>
                            <FooterLink to="/" onClick={() => {
                                window.location.replace('http://maude.sip.ucm.es/fadoss');
                                return null;
                            }}>Research Group</FooterLink>
                            <FooterLink to="/">FAQs</FooterLink>
                            <div className="tooltip">
                                <FooterLink to="/">Contact us</FooterLink>
                                <div className="bottom">
                                    <span>joncarre@ucm.es</span>
                                    <CopyButton className={isCopied ? "copied" : ""} onClick={() => handleCopy()}>{copyText}</CopyButton>
                                    <i></i>
                                </div>
                            </div>
                        </FooterLinkItems>
                    </FooterLinksWrapper>
                    <FooterLinksWrapper>
                        <FooterLinkItems>
                            <FooterLinkTitle>The DApp</FooterLinkTitle>
                            <FooterLink to="/">How it works</FooterLink>
                            <FooterLink to="/">Examples</FooterLink>
                            <FooterLink to="/">Terms of Service</FooterLink>
                        </FooterLinkItems>
                    </FooterLinksWrapper>
                </FooterLinksContainer>
                <WebsiteRights>
                    J. Carrero Â© {new Date().getFullYear()} All rights reserved.
                </WebsiteRights>
            </FooterWrap>
        </FooterContainer>
    );
};

export default Footer;
