import './App.css';
import { useState, useEffect } from 'react';
import { useForm } from "react-hook-form";
import { ethers } from 'ethers';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

import Generator from './artifacts/contracts/Generator.sol/Generator.json'
import Navbar from "./components/Navbar/index";
import Sidebar from "./components/SideBar";
import Home from "./pages";
import RegisterPage from "./pages/register";
import UserSection from './components/UserSection';

// Update with the contract address logged out to the CLI when it was deployed 
export const generatorAddress = "0x6e4B3A201532D0FfD2d3C821371b009aA113bFcE";

// request access to the user's MetaMask account
export async function requestAccount() {
  await window.ethereum.request({ method: 'eth_requestAccounts' });
}

  export async function async_getResearcher(data) {
      if (typeof window.ethereum !== 'undefined') {
        const provider = new ethers.providers.Web3Provider(window.ethereum)
        const contract = new ethers.Contract(generatorAddress, Generator.abi, provider);
        try {
          const signer = provider.getSigner()
          const result = await contract.connect(signer).getResearcher(data.orcid)
          console.log("async_getResearcher: " + result)
        } catch (err) {
          console.log("Error: ", err)
        }
      }    
    }

function App() {
  return (
      <Router>
        <Switch >
          <Route path='/' component={Home} exact />
          <Route path='/register' component={RegisterPage} exact />
          <Route path='/user' component={UserSection} exact />
        </Switch>
      </Router>
  );
}

export default App;
