import './App.css';
import { useState, useEffect } from 'react';
import { useForm } from "react-hook-form";
import { ethers } from 'ethers';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

import Generator from './artifacts/contracts/Generator.sol/Generator.json'
import Navbar from "./components/Navbar/index";
import Sidebar from "./components/SideBar";

import Home from "./pages";
import RegisterPage from "./pages/register";
import UserSectionPage from './pages/user';
import SigninPage from './pages/signin';
import InstancesPage from './pages/instances';
import InstancePage from './pages/instance';
import FooterSubpage from './pages/footerSubpage';

import TermsOfService from './components/Footer/SubPages/TermsOfService';

// Update with the contract address logged out to the CLI when it was deployed 
export const generatorAddress = "0x2Dc04cf7B5dD36146CECA94B9e76876889B27E1C";

// request access to the user's MetaMask account
export async function requestAccount() {
  await window.ethereum.request({ method: 'eth_requestAccounts' });
}

function App() {
  const [isOpen, setIsOpen] = useState(false)

  const toggle = () => {
    setIsOpen(!isOpen)
  }
  return (
    <Router>
      <Sidebar isOpen={isOpen} toggle={toggle} />
      <Navbar toggle={toggle} />
      <Switch>
        <Route path='/' component={Home} exact />
        <Route path='/register' component={RegisterPage} exact />
        <Route path='/signin' component={SigninPage} exact />
        <Route path='/user' component={UserSectionPage} exact />
        <Route path='/instances' component={InstancesPage} exact />
        <Route path='/instance' component={InstancePage} exact />
        <Route path='/termsofservice' component={FooterSubpage} Comp={TermsOfService} exact />
        {/* <Route path='/faqs' component={Faqs} exact /> */}
        {/* <Route path='/howitworks' component={HowItWorks} exact /> */}
        {/* <Route path='/examples' component={Examples} exact /> */}
      </Switch>
    </Router>
  );
}

export default App;
