// Creates the structure for a JSON file
export const buildJSON = _oldChain => {
    let object = {};
    let arrayOld = Array.from(_oldChain);
    let arrayValue = new Array(arrayOld.length);
    let arraySign = new Array(arrayOld.length);
    for (let i = 0; i < arrayOld.length; i++) {
        arrayValue[i] = parseInt(arrayOld[i]);
        if (Math.random() < 0.5) {
            arraySign[i] = 1;
        }
        else {
            arraySign[i] = 0;
            object.value = arrayValue;
        }
    }
    object.sign = arraySign;
    return JSON.stringify(object);
}

export const downloadJSON = json => {
    const element = document.createElement("a");
    const textFile = new Blob([json], { type: 'text/plain' }); //pass data from localStorage API to blob
    element.href = URL.createObjectURL(textFile);
    element.download = "chain.json";
    document.body.appendChild(element);
    element.click();
}
