import './App.css';
import { useState } from 'react';
import { ethers } from 'ethers';
import Generator from './artifacts/contracts/Generator.sol/Generator.json'


// Update with the contract address logged out to the CLI when it was deployed 
const generatorAddress = "0x36f090DA787c9c5d50C758B616e6154decA995F1"

function App() {
  // store greeting in local state
  const [data, setData] = useState()
  const [userInput, setUserInput] = useState()
  const [hashInput, setHashInput] = useState()

    // request access to the user's MetaMask account
    async function requestAccount() {
      await window.ethereum.request({ method: 'eth_requestAccounts' });
    }

    // call the smart contract, read the current value
    async function asyncFetchResearcher() {
      if (typeof window.ethereum !== 'undefined') {
        const provider = new ethers.providers.Web3Provider(window.ethereum)
        const contract = new ethers.Contract(generatorAddress, Generator.abi, provider);
        try {
          const signer = provider.getSigner()
          const result = await contract.connect(signer).getResearcher()
          setData(result)
          console.log('data: ', result)
        } catch (err) {
          console.log("Error: ", err)
        }
      }    
    }

    // call the smart contract, send a new researcher
    async function asyncRegResearcher() {
      if (!userInput) return
      if (typeof window.ethereum !== 'undefined') {
        await requestAccount()
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner()
        const contract = new ethers.Contract(generatorAddress, Generator.abi, signer);
        const transaction = await contract.solveLastInstance(userInput)
        await transaction.wait()
      }
    }

    // ----------------------------

    // call the smart contract, read the current value
    async function asyncFetchHash() {
      if (typeof window.ethereum !== 'undefined') {
        const provider = new ethers.providers.Web3Provider(window.ethereum)
        const contract = new ethers.Contract(generatorAddress, Generator.abi, provider);
        try {
          const signer = provider.getSigner()
          const result = await contract.connect(signer).getLastHash()
          setData(result)
          console.log('data: ', result)
        } catch (err) {
          console.log("Error: ", err)
        }
      }    
    }

    // call the smart contract, send a new researcher
    async function asyncSetLastHash() {
      if (!hashInput) return
      if (typeof window.ethereum !== 'undefined') {
        await requestAccount()
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner()
        const contract = new ethers.Contract(generatorAddress, Generator.abi, signer);
        const transaction = await contract.regResearcher(hashInput)
        await transaction.wait()
      }
    }

  return (
    <div className="App">
      <h5>Form</h5>
      <button onClick={asyncFetchResearcher}>Fetch Researcher</button>
      <p> { data } </p>
      <button onClick={asyncRegResearcher}>Set researcher</button>
      <input onChange={e => setUserInput(e.target.value)} placeholder="name, orcid" />

      <h5>Researcher information</h5>
      <button onClick={asyncFetchHash}>Fetch hash</button>
      <p> { data } </p>
      <button onClick={asyncSetLastHash}>Set hash</button>
      <input onChange={e => setHashInput(e.target.value)} placeholder="name, orcid" />
    </div>
  );
}

export default App;
