import Generator from './artifacts/contracts/Generator.sol/Generator.json'
import Navbar from "./components/Navbar/index";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Sidebar from "./components/SideBar";
import Home from "./pages";
import SigninPage from "./pages/signin";
import RegisterPage from "./pages/register";

// Update with the contract address logged out to the CLI when it was deployed 
const generatorAddress = "0x6e4B3A201532D0FfD2d3C821371b009aA113bFcE"

function App() {

  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm();

  const { 
    register: foo, 
    handleSubmit: handleFoo, 
    formState: { errorsss }
  } = useForm();

  const [researcher, setResearcher] = useState({
    orcid: null,
    registered: null,
    idInstance: null
  })

  const [instance, setInstance] = useState({
    id: null,
    chain: null,
    size: null,
    dateCreated: null,
    solution: null,
    solved: null,
    dateSolution: null
  })

  // request access to the user's MetaMask account
  async function requestAccount() {
    await window.ethereum.request({ method: 'eth_requestAccounts' });
  }

    async function async_setResearcher(data) {
      if (typeof window.ethereum !== 'undefined') {
        await requestAccount()
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner()
        const contract = new ethers.Contract(generatorAddress, Generator.abi, signer);
        console.log(data.firstName, data.age);
        const transaction = await contract.setResearcher(data.firstName, data.age)
        await transaction.wait()
      }
    }

    async function async_getResearcher(data) {
      if (typeof window.ethereum !== 'undefined') {
        const provider = new ethers.providers.Web3Provider(window.ethereum)
        const contract = new ethers.Contract(generatorAddress, Generator.abi, provider);
        try {
          const signer = provider.getSigner()
          const result = await contract.connect(signer).getResearcher(data.orcid)
          console.log(result)
        } catch (err) {
          console.log("Error: ", err)
        }
      }    
    }

  return (
    <div className="App">
      <form onSubmit={handleFoo(async_getResearcher)}>
