import './App.css';
import { useState } from 'react';
import { ethers } from 'ethers';
const hre = require("hardhat");


// Update with the contract address logged out to the CLI when it was deployed 
const generatorAddress = "0x76410EB8a21CcbA717Ecfbe8cB634E2c918108d5"

function App() {
  // store greeting in local state
  const [researcher, regResearcherValue] = useState()

    // request access to the user's MetaMask account
    async function requestAccount() {
      await window.ethereum.request({ method: 'eth_requestAccounts' });
    }

    // call the smart contract, read the current greeting value
    async function asyncFetchResearcher() {
      if (typeof window.ethereum !== 'undefined') {
        const provider = new ethers.providers.Web3Provider(window.ethereum)
        const contract = await hre.getContractAt("Generator", generatorAddress)
        try {
          const data = await contract.getResearcher()
          console.log('data: ', data)
        } catch (err) {
          console.log("Error: ", err)
        }
      }    
    }

  // call the smart contract, send a new researcher
  async function asyncRegResearcher() {
    if (!researcher) return
    if (typeof window.ethereum !== 'undefined') {
      await requestAccount()
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner()
      const contract = await hre.getContractAt("Generator", generatorAddress)
      const transaction = await contract.regResearcher(researcher, 212121)
      await transaction.wait()
      asyncFetchResearcher()
    }
  }

  return (
    <div className="App">
                            <p>Register your information</p>
                            <label htmlFor='for'>Name</label>
                            <input type='text'/>
                            <label htmlFor='for'>ORCID</label>
                            <input type='text'/>
                            <button onClick={asyncRegResearcher}>Register</button>
                            <input onChange={e => regResearcherValue(e.target.value)} placeholder="name, orcid" />
                            <button type='submit'>Register</button>
                            <button onClick={asyncFetchResearcher}>Fetch Researcher</button>
    </div>
  );
}

export default App;
