import React from 'react'
import { useForm } from "react-hook-form";
import { 
    Container, 
    Form, 
    FormButton,
    FormError
 } from './RegisterElements';

 import {
    FormInput, 
    FormWrap,
    FormH1,
    FormLabel,
    FormContent,
 } from '../Signin/SigninElements'

const Register = () => {
    const {
        register,
        handleSubmit,
        watch,
        formState: { errors }
      } = useForm();
    
      const onSubmit = (data) => {
        alert(JSON.stringify(data));
      }; // your form submit function which will invoke after successful validation

      
    return (
        <>
            <Container>
                <FormWrap>
                    <FormContent>
                        <Form onSubmit={handleSubmit(onSubmit)}>
                            <FormH1>Register your information</FormH1>
                            <FormLabel>Name</FormLabel>
                            <FormInput {...register("firstName", {
                                required: true,
                                maxLength: 20,
                                pattern: /^[A-Za-z]+$/i
                            })}/>
                                {errors?.firstName?.type === "required" && <FormError>This field is required</FormError>}
                                {errors?.firstName?.type === "maxLength" && <FormError>First name cannot exceed 20 characters</FormError> }
                                {errors?.firstName?.type === "pattern" && <FormError>Alphabetical characters only</FormError> }
                            <FormLabel>ORCID</FormLabel>
                            <FormInput {...register("orcid", { 
                                required: true,
                                minLength: 16, 
                                maxLength: 16,
                                pattern: /^[0-9\b]+$/
                            })} />
                                {errors?.orcid.type === "required" && <FormError>This field is required</FormError>}
                                {errors?.orcid.type == "minLength" && "maxLength" && <FormError>It must be a number of 16 digits</FormError> }
                                {errors?.orcid.type === "pattern" && <FormError>Number digits only</FormError> }
                            <FormButton type="submit" />
                        </Form>
                    </FormContent>
                </FormWrap>
            </Container>
        </>
    )
}

export default Register;

