import { React } from 'react';
import { useForm } from 'react-hook-form';
import { generatorAddress, requestAccount } from '../../App.js';
import { ethers } from 'ethers';
import Generator from '../../artifacts/contracts/Generator.sol/Generator.json';
import {
    Container,
    FormButton,
    FormButton1,
    FormButton2,
    FormInput1,
    FormInput2
} from './UserElements'
import {
    FormWrap,
    FormContent,
    Form,
    FormH1,
    FormLabel,
    FormInput,
    FormError
} from '../Register/RegisterElements'

const UserSection = () => {
    const {
        register,
        handleSubmit,
        reset,
        formState: { errors }
    } = useForm();


    

    return (
        <Container>
            <FormWrap>
                <FormContent>
                    <Form onSubmit={handleSubmit()}>
                        <FormH1>Create a new instance</FormH1>
                        <div>
                            <FormLabel>First parameter</FormLabel>
                            <FormInput1 {...register("p_value", { 
                                required: true,
                                max: 99,
                                min: 1,
                                pattern: /^[0-9\b]+$/
                            })} type="number" name="p_value"/>
                                {errors?.p_value?.type === "required" && <FormError>Field required</FormError>}
                                {errors?.p_value?.type === "max" && <FormError>Integer in [1, 99]</FormError> }
                                {errors?.p_value?.type === "min" && <FormError>Integer in [1, 99]</FormError> }
                                {errors?.p_value?.type === "pattern" && <FormError>Numerical characters</FormError> }
                            <FormLabel>Second parameter</FormLabel>
                            <FormInput2 {...register("q_value", { 
                                required: true,
                                max: 99,
                                min: 1,
                                pattern: /^[0-9\b]+$/
                            })} type="number" name="q_value"/>
                                {errors?.q_value?.type === "required" && <FormError>Field required</FormError>}
                                {errors?.q_value?.type === "max" && <FormError>Integer in [1, 99]</FormError> }
                                {errors?.q_value?.type === "min" && <FormError>Integer in [1, 99]</FormError> }
                                {errors?.q_value?.type === "pattern" && <FormError>Numerical characters</FormError> }
                        </div>       
                        <div>
                            <FormButton1 type="submit"  value="A generator"/>
                            <FormButton2 type="submit"  value="B generator"/>
                        </div>
                    </Form>
                </FormContent>

                <FormContent>
                    <Form onSubmit={handleSubmit()}>
                        <FormH1>Search for instances</FormH1>
                        <FormLabel>ORCID</FormLabel>
                        <FormInput {...register("orcid", { 
                            required: true,
                            minLength: 16, 
                            maxLength: 16,
                            pattern: /^[0-9\b]+$/
                        })} type="number" name="orcid"/>
                            {errors?.orcid?.type === "required" && <FormError>This field is required</FormError>}
                            {errors?.orcid?.type === "minLength" && <FormError>It must be a number of 16 digits</FormError> }
                            {errors?.orcid?.type === "maxLength" && <FormError>It must be a number of 16 digits</FormError> }
                            {errors?.orcid?.type === "pattern" && <FormError>Numerical characters only</FormError> }
                        <FormButton type="submit" value="Get instances"/>
                    </Form>
                </FormContent>
            </FormWrap>
        </Container>
    )
}

export default UserSection;
