import './App.css';
import { useState } from 'react';
import { ethers } from 'ethers';
import Generator from './artifacts/contracts/Generator.sol/Generator.json'


// Update with the contract address logged out to the CLI when it was deployed 
const generatorAddress = "0xB32F3280feDDa9E91A4784FC2Fb8d3675F90cB12"

function App() {
  // store greeting in local state
  const [researcher, ResearcherValue] = useState()
  const data = null;

    // request access to the user's MetaMask account
    async function requestAccount() {
      await window.ethereum.request({ method: 'eth_requestAccounts' });
    }

    // call the smart contract, read the current value
    async function asyncFetchResearcher() {
      if (typeof window.ethereum !== 'undefined') {
        const provider = new ethers.providers.Web3Provider(window.ethereum)
        const contract = new ethers.Contract(generatorAddress, Generator.abi, provider);
        try {
          const signer = provider.getSigner()
          data = contract.connect(signer).getResearcher()
          console.log('data: ', data)
        } catch (err) {
          console.log("Error: ", err)
        }
      }    
    }

    // call the smart contract, send a new researcher
    async function asyncRegResearcher() {
      if (!researcher) return
      if (typeof window.ethereum !== 'undefined') {
        await requestAccount()
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner()
        const contract = new ethers.Contract(generatorAddress, Generator.abi, signer);
        const transaction = await contract.regResearcher(researcher)
        await transaction.wait()
      }
    }

    async function asyncSetLastSolution() {
      if (!researcher) return
      if (typeof window.ethereum !== 'undefined') {
        await requestAccount()
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner()
        const contract = new ethers.Contract(generatorAddress, Generator.abi, signer);
        const transaction = await contract.solveLastInstance(researcher)
        await transaction.wait()
      }
    }

  return (
    <div className="App">
      <p>Form</p>
      <button onClick={asyncFetchResearcher}>Fetch Researcher { data } </button>
      <button onClick={asyncRegResearcher}>Register</button>
      <input onChange={e => ResearcherValue(e.target.value)} placeholder="name, orcid" />
      <p>Researcher information</p>
      <button>Check instance</button>
      <button onClick={asyncSetLastSolution}>Set hash</button>
      <input onChange={e => ResearcherValue(e.target.value)} placeholder="hash" />
    </div>
  );
}

export default App;
