import * as React from "react";
import { styled } from "@mui/system";

const cellHeight = 30;

const blue = {
  200: "#A5D8FF",
  400: "#3399FF"
};

const grey = {
  50: "#F3F6F9",
  100: "#E7EBF0",
  200: "#E0E3E7",
  300: "#CDD2D7",
  400: "#B2BAC2",
  500: "#A0AAB4",
  600: "#6F7E8C",
  700: "#3E5060",
  800: "#2D3843",
  900: "#1A2027"
};

const Root = styled("div")(
  ({ theme }) => `
  table {
    font-size: 0.8rem;
    border-collapse: collapse;
    width: 500px;
    @media (max-width: 600px) {
      width: 100%;
    }
  }

  td,
  th {
    border: 1px solid ${theme.palette.mode === "dark" ? grey[800] : grey[300]};
    text-align: center;
    padding: 6px;
  }

  th {
    background-color: rgb(98, 129, 123, 0.8);
    color: white;
    height: ${`${cellHeight}px`};
    width: 100px;
  }

  td {
    background-color: #1b2631;
    color: white;
    height: ${`${cellHeight}px`};
  }
  `
);

// props: data & headerItems
export default function UnstyledTable({ data, headerItems }) {
  const handleResolve = (data, item) => {
    if (item.name === 'solved') {
      return data[item.name] ? <td>True</td> : <td>False</td>;
    }
    return <td>{data[item.name]}</td>
  }
  return (
    <Root sx={{}}>
      <table aria-label="custom table">
        {headerItems.map((item, key) => {
          return (
            <tr key={key}>
              <th>{item.label}</th>
              {handleResolve(data, item)}
            </tr>
          );
        })

        }
      </table>
    </Root>
  );
}
