import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { useForm } from 'react-hook-form';
import ColumnHeaderTable from "../ColumnHeaderTable";
import {
    Container,
    Wrap,
    TableSection,
    FormWrap1,
    FormInput,
    FormH1,
    Form,
    FormButton
} from './InstanceElements'
import './messagesInfo.css';

import {
    FormError
} from '../FormElements';

const Instance = props => {
    const location = useLocation();
    const [rowData, setRowData] = useState({});

    const {
        register,
        handleSubmit,
        reset,
        formState: { errors }
    } = useForm();

    useEffect(() => {
        if (location.state) {
            setRowData(location.state.rowData);
        }
    }, [location.state])

    const headerItems = [
        { name: "id", label: "ID" },
        { name: "chain", label: "Chain" },
        { name: "size", label: "Size" },
        { name: "created", label: "Date Created" },
        { name: "solution", label: "Solution" },
        { name: "solved", label: "Solved" },
        { name: "ended", label: "Date Solution" },
    ]

    const handleResolve = (data) => {
        console.log(data);
        reset({ s_hash: "", a_hash: "", hash_method: "" });
        // delete the line below and import resolve instance API
        // setRowData({ ...rowData, solved: true });
    }

    return (
        <Container>
            <Wrap>
                <TableSection>
                    <ColumnHeaderTable headerItems={headerItems} data={rowData} footer></ColumnHeaderTable>
                </TableSection>
            </Wrap>
            {/* solved === false */}
            {Object.keys(rowData).length > 0 && !rowData.solved &&
                <FormWrap1>
                    <Form key={1} onSubmit={handleSubmit(handleResolve)}>
                        <FormH1>Resolve the instance</FormH1>
                        <span className="field-tip">
                            <span className="tip-content">Hash of the file containing the solution.</span>
                            <FormInput {...register("s_hash", {
                                required: true,
                                pattern: /^[0-9\b]+$/
                            })} type="text" name="s_hash" placeholder="Solution hash" />
                            {errors?.s_hash?.type === "required" && <FormError>Field required</FormError>}
                            {errors?.s_hash?.type === "pattern" && <FormError>Numerical characters only</FormError>}
                        </span>
                        <span className="field-tip">
                            <span className="tip-content">The "q" value indicates the probability of generating a new clause</span>

                            <FormInput {...register("a_hash", {
                                required: true,
                                pattern: /^[0-9\b]+$/
                            })} type="text" name="a_hash" placeholder="Algorithm hash" />
                            {errors?.a_hash?.type === "required" && <FormError>Field required</FormError>}
                            {errors?.a_hash?.type === "pattern" && <FormError>Numerical characters only</FormError>}
                        </span>
                        <span className="field-tip">
                            <span className="tip-content">Enter the number of instances you would like to generate</span>

                            <FormInput {...register("hash_method", {
                                required: true,
                                pattern: /^[0-9\b]+$/
                            })} type="text" name="hash_method" placeholder="Hash method" />
                            {errors?.hash_method?.type === "required" && <FormError>Field required</FormError>}
                            {errors?.hash_method?.type === "pattern" && <FormError>Numerical characters only</FormError>}
                        </span>
                        <FormButton id="submit1" type="submit" value="Submit data" />
                    </Form>
                </FormWrap1>
            }
        </Container>
    );
}

export default Instance;
